mapper.select:
select document_code as documentCode, emp_name as empName, document_date as documentDate, document_title as documentTitle, document_type_code as documentTypeCode, document_type_name as documentTypeName
from tbl_document join tbl_emp using (emp_code) join tbl_document_type using (document_type_code)
where document_type_code = #{_string}
order by document_date desc

mapper.update:
update
   tbl_document_sign
   set sign_yn=#{check}
where emp_code=#{empCode} and document_code=#{documentCode}

update tbl_spending
	set approve=1
where document_code=#{_string}
