mapper.select:
select count(*)
  from tbl_dept_leader
 where emp_code = #{empCode}

mapper.update:
update tbl_emp
   set team_code = #{teamCode},
       rank_code = #{rankCode}
 where emp_code = #{empCode}

mapper.insert:
 	 insert into tbl_salary
	   (
	   	emp_code,
	   	salary
	   )
  	values
	   (
	   	#{empCode},
	   	#{salary}
	   )

 	insert into tbl_chat_group
 	(
 	group_no,
 	emp_code
 	)
 	values(
	#{groupNo}, 		
 	#{empCode}
 	)

mapper.update:
update tbl_emp
   set team_code = #{teamCode},
   	   rank_code = #{rankCode}
 where emp_code = #{empCode}

update tbl_salary
   set salary = #{salary}
 where emp_code = #{empCode}

update tbl_vacation_count
   set total_vacation = #{vacationTotal},
       use_vacation = #{vacationUse}
 where emp_code = #{empCode}

update tbl_chat_group
   set read_count = read_count +1
 where group_no = #{groupNo} 
   and emp_code = #{empCode}

update tbl_chat_group
   set read_count = 0
 where group_no = #{groupNo} 
   and emp_code != #{empCode}

 	 UPDATE tbl_daily_report
SET report_content = #{reportContent}
WHERE emp_code = #{empCode}
  AND report_date = TRUNC(SYSDATE)

update tbl_alarm
   set alarm_read = 'y'
 where alarm_no = #{alarmNo}

mapper.select:
select count(*)
  from tbl_check
 where att_date = #{day} and emp_code = #{empCode}

mapper.update:
update tbl_check
   set check_out = TO_CHAR(SYSDATE, 'HH24MISS')
 where att_date = #{day} and emp_code = #{empCode}

update tbl_emp
   set emp_name = #{empName},
    	emp_phone = #{empPhone}
 where emp_code = #{empCode}

update tbl_emp
   set emp_pw = #{empPw}
 where emp_code = #{empCode}

mapper.select:
select count(*)
  from tbl_day_price
 where team_code = #{teamCode} and rank_code = #{rankCode}

mapper.update:
update tbl_day_price
   set price = #{price}
 where team_code = #{teamCode} and rank_code = #{rankCode}
