mapper.select:
select count(*)
  from tbl_dept_leader
 where emp_code = #{empCode}

mapper.update:
update tbl_emp
   set team_code = #{teamCode},
       rank_code = #{rankCode}
 where emp_code = #{empCode}

mapper.insert:
 	 insert into tbl_salary
	   (
	   	emp_code,
	   	salary
	   )
  	values
	   (
	   	#{empCode},
	   	#{salary}
	   )
